// Parse AI intent analysis and merge with context
const aiResponse = $input.first().json;

// Extract JSON from AI response (handle different formats)
let aiAnalysis = {};
let aiContent = '';

if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message) {
  aiContent = aiResponse.choices[0].message.content;
} else if (aiResponse.message && aiResponse.message.content) {
  aiContent = aiResponse.message.content;
} else if (aiResponse.content) {
  aiContent = aiResponse.content;
} else if (aiResponse.output) {
  aiContent = aiResponse.output;
} else if (typeof aiResponse === 'string') {
  aiContent = aiResponse;
} else {
  aiAnalysis = aiResponse;
}

// Parse JSON from AI response if needed
if (aiContent) {
  aiContent = aiContent.trim();
  aiContent = aiContent.replace(/```json\n?/g, '');
  aiContent = aiContent.replace(/```\n?/g, '');

  try {
    aiAnalysis = JSON.parse(aiContent);
  } catch (e) {
    const jsonMatch = aiContent.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      try {
        aiAnalysis = JSON.parse(jsonMatch[0]);
      } catch (e2) {
        aiAnalysis = {
          intent: 'general',
          needsLostItems: false,
          needsMyReports: false,
          needsFoundItems: false,
          searchTerm: null,
          reasoning: 'Failed to parse AI response'
        };
      }
    } else {
      aiAnalysis = {
        intent: 'general',
        needsLostItems: false,
        needsMyReports: false,
        needsFoundItems: false,
        searchTerm: null,
        reasoning: 'No valid JSON found'
      };
    }
  }
}

// Get context from previous node
const context = $input.first().json || {};

// Merge AI analysis with context
return [{
  json: {
    ...context,
    ...aiAnalysis,
    studentNo: aiAnalysis.studentNo || context.studentNo || '',
    searchTerm: aiAnalysis.searchTerm || null
  }
}];

// Route to appropriate HTTP nodes based on AI analysis flags
const data = $input.first().json || {};
const needsLostItems = data.needsLostItems === true;
const needsMyReports = data.needsMyReports === true;
const needsFoundItems = data.needsFoundItems === true;

const outputs = [];

// Route to Get Lost Items if needed
if (needsLostItems) {
  outputs.push({
    json: {
      ...data,
      queryType: 'lost_items'
    }
  });
}

// Route to Get My Reports if needed
if (needsMyReports) {
  outputs.push({
    json: {
      ...data,
      queryType: 'my_reports'
    }
  });
}

// Route to Get Found Items if needed
if (needsFoundItems) {
  outputs.push({
    json: {
      ...data,
      queryType: 'found_items'
    }
  });
}

// If no queries needed, send to Merge directly (general query)
if (outputs.length === 0) {
  outputs.push({
    json: {
      ...data,
      queryType: 'none'
    }
  });
}

return outputs;

// Prepare data for AI response generation
const mergedData = $input.all();

// Get context from Smart Router (via Route Intent false output or from previous nodes)
let context = {};
let aiAnalysis = {};

// Check if we have Smart Router data (from Route Intent false output for general queries)
mergedData.forEach((item) => {
  const data = item.json || {};
  // Smart Router data has originalMessage and AI analysis fields
  if (data.originalMessage !== undefined && data.intent !== undefined) {
    context = {
      originalMessage: data.originalMessage || '',
      studentName: data.studentName || '',
      studentNo: data.studentNo || '',
      conversationHistory: data.conversationHistory || []
    };
    aiAnalysis = {
      intent: data.intent || 'general',
      needsLostItems: data.needsLostItems || false,
      needsMyReports: data.needsMyReports || false,
      needsFoundItems: data.needsFoundItems || false,
      searchTerm: data.searchTerm || null,
      reasoning: data.reasoning || ''
    };
  }
});

// If no context found, get from Smart Router node
if (!context.originalMessage) {
  try {
    const routerData = $('Smart Router').first().json || {};
    context = {
      originalMessage: routerData.originalMessage || '',
      studentName: routerData.studentName || '',
      studentNo: routerData.studentNo || '',
      conversationHistory: routerData.conversationHistory || []
    };
    aiAnalysis = {
      intent: routerData.intent || 'general',
      needsLostItems: routerData.needsLostItems || false,
      needsMyReports: routerData.needsMyReports || false,
      needsFoundItems: routerData.needsFoundItems || false,
      searchTerm: routerData.searchTerm || null,
      reasoning: routerData.reasoning || ''
    };
  } catch (e) {
    // Fallback to Prepare Context
    try {
      const prevContext = $('Prepare Context').first().json || {};
      context = {
        originalMessage: prevContext.originalMessage || '',
        studentName: prevContext.studentName || '',
        studentNo: prevContext.studentNo || '',
        conversationHistory: prevContext.conversationHistory || []
      };
    } catch (e2) {
      // Use defaults
    }
  }
}

// Collect database results from HTTP Request nodes
// HTTP Request nodes don't have originalMessage field
const dbResults = [];
mergedData.forEach((item) => {
  const data = item.json || {};
  // Skip Smart Router data (has originalMessage)
  if (data.originalMessage === undefined) {
    // This is from an HTTP Request node
    if (data.data && Array.isArray(data.data)) {
      dbResults.push(...data.data);
    } else if (data.success && data.data) {
      if (Array.isArray(data.data)) {
        dbResults.push(...data.data);
      } else if (data.data) {
        dbResults.push(data.data);
      }
    }
  }
});

return [{
  json: {
    originalMessage: context.originalMessage || '',
    studentName: context.studentName || '',
    studentNo: context.studentNo || '',
    conversationHistory: context.conversationHistory || [],
    aiAnalysis: aiAnalysis,
    dbResults: dbResults,
    dbResultsCount: dbResults.length
  }
}];

