  // Format AI analysis
  const analysisText = aiAnalysis && Object.keys(aiAnalysis).length > 0
    ? JSON.stringify(aiAnalysis, null, 2)
    : 'No intent analysis available.';

  return `User Message: ${message}${historyText}\n\nStudent: ${studentName} (${studentNo})\n\nAI Intent Analysis:\n${analysisText}\n\nDatabase Results:\n${dbResultsText}\n\nProvide a natural, helpful response based on the user's question and the available data.`;
})() }}

