// Extract reply from AI response
const aiNode = $input.first();
const aiResponse = aiNode.json;

// Extract reply from OpenAI response - handle different response formats
let reply = '';
if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message) {
  reply = aiResponse.choices[0].message.content;
} else if (aiResponse.message && aiResponse.message.content) {
  reply = aiResponse.message.content;
} else if (aiResponse.content) {
  reply = aiResponse.content;
} else if (aiResponse.output) {
  reply = aiResponse.output;
} else if (aiResponse.text) {
  reply = aiResponse.text;
} else if (typeof aiResponse === 'string') {
  reply = aiResponse;
} else {
  reply = 'I apologize, but I couldn\'t generate a response. Please try again.';
}

// Clean up reply (remove markdown if present)
reply = reply.trim();
reply = reply.replace(/\*\*(.*?)\*\*/g, '$1'); // Remove bold
reply = reply.replace(/\*(.*?)\*/g, '$1'); // Remove italic
reply = reply.replace(/```[\s\S]*?```/g, ''); // Remove code blocks

// Get context
const preparedData = $('Prepare AI Response').first().json || {};
const intent = preparedData.aiAnalysis?.intent || 'general';
const resultCount = preparedData.dbResultsCount || 0;

return [{
  json: {
    reply: reply,
    intent: intent,
    hasResults: resultCount > 0,
    resultCount: resultCount,
    timestamp: new Date().toISOString()
  }
}];

const error = $input.first().json.error;
const errorMessage = error?.message || error?.error || 'Unknown error';

return [{
  json: {
    reply: 'I encountered an error processing your request. Please try again or contact admin for assistance. You can also use the dashboard to report items directly.',
    error: errorMessage,
    timestamp: new Date().toISOString()
  }
}];
