const input = $input.first().json;

// Extract approval data
const action = input.action || ''; // 'approve' or 'reject'
const type = input.type || ''; // 'report', 'found_item', 'profile_photo'
const itemID = input.itemID || input.reportID || input.photoID || '';
const adminID = input.adminID || '';
const studentNo = input.studentNo || '';
const itemName = input.itemName || input.ItemName || '';
const studentName = input.studentName || input.StudentName || '';
const studentEmail = input.studentEmail || input.Email || '';

return [{
  json: {
    action: action,
    type: type,
    itemID: itemID,
    adminID: adminID,
    studentNo: studentNo,
    itemName: itemName,
    studentName: studentName,
    studentEmail: studentEmail,
    originalData: input
  }
}];

const data = $('Extract Approval Data').first().json;
const isApproved = $('Check Action Type').first().json.action === 'approve';

let notificationType = '';
let title = '';
let message = '';

if (data.type === 'report') {
  notificationType = isApproved ? 'report_approved' : 'report_rejected';
  title = isApproved ? 'Lost Item Report Approved!' : 'Lost Item Report Rejected';
  message = isApproved 
    ? `Your lost item report for '${data.itemName}' has been approved and is now visible to other users.`
    : `Your lost item report for '${data.itemName}' was rejected. Please contact admin for more information.`;
} else if (data.type === 'found_item') {
  notificationType = isApproved ? 'item_approved' : 'item_rejected';
  title = isApproved ? 'Found Item Approved!' : 'Found Item Rejected';
  message = isApproved 
    ? `Your found item '${data.itemName}' has been approved and is now visible to students.`
    : `Your found item '${data.itemName}' was rejected.`;
} else if (data.type === 'profile_photo') {
  notificationType = isApproved ? 'photo_approved' : 'photo_rejected';
  title = isApproved ? 'Profile Photo Approved!' : 'Profile Photo Rejected';
  message = isApproved 
    ? 'Your profile photo has been approved and is now visible to other users.'
    : 'Your profile photo was rejected. Please upload a new photo.';
}

return [{
  json: {
    studentNo: data.studentNo,
    type: notificationType,
    title: title,
    message: message,
    relatedID: data.itemID,
    action: data.action,
    itemName: data.itemName,
    studentName: data.studentName,
    studentEmail: data.studentEmail
  }
}];

const data = $('Format Notification').first().json;
return [{
  json: {
    success: true,
    message: `Notification sent to ${data.studentName} (${data.studentEmail})`,
    notificationType: data.type,
    action: data.action,
    timestamp: new Date().toISOString()
  }
}];

const error = $input.first().json.error;
return [{
  json: {
    success: false,
    error: error?.message || error?.error || 'Unknown error',
    message: 'Failed to send approval notification'
  }
}];
