// Get input from webhook - simple JSON now
const input = $input.first().json || $input.first() || {};
const webhookData = input.body || input.json || input || {};

// Extract photoURL (string, not binary)
const photoURL = webhookData.photoURL || input.photoURL || null;

console.log('Photo URL received:', photoURL ? 'YES' : 'NO');
if (photoURL) {
  console.log('Photo URL:', photoURL);
}

return [{
  json: {
    action: 'create_lost_report',
    studentNo: webhookData.studentNo || input.studentNo || '',
    itemName: webhookData.itemName || input.itemName || '',
    itemClass: webhookData.itemClass || input.itemClass || '',
    description: webhookData.description || input.description || '',
    dateOfLoss: webhookData.dateOfLoss || input.dateOfLoss || '',
    lostLocation: webhookData.lostLocation || input.lostLocation || '',
    photoURL: photoURL  // String URL, not base64
  }
}];

// Safely extract response data
const input = $input.first();
const response = input?.json || input || {};

// Handle error responses
if (response.error || response.success === false) {
  return [{
    json: {
      success: false,
      message: response.message || response.error || 'Failed to create lost item report',
      error: response.error || 'Unknown error'
    }
  }];
}

// Handle success responses
return [{
  json: {
    success: response.success !== undefined ? response.success : true,
    message: response.message || 'Lost item report processed successfully',
    data: response.data || response.id || null
  }
}];

const error = $input.first().json.error;
return [{
  json: {
    success: false,
    error: error?.message || error?.error || 'Unknown error',
    message: 'Failed to create lost item report'
  }
}];
