// Get input from webhook - data is in body property
const input = $input.first().json || $input.first() || {};
const webhookData = input.body || input.json || input || {};

// Extract lost item details
const reportID = webhookData.reportID || webhookData.ReportID || input.reportID || input.ReportID || '';
const itemName = webhookData.itemName || webhookData.ItemName || input.itemName || input.ItemName || '';
const itemClass = webhookData.itemClass || webhookData.ItemClass || webhookData.ClassName || input.itemClass || input.ItemClass || input.ClassName || '';
const description = webhookData.description || webhookData.Description || input.description || input.Description || '';
const lostLocation = webhookData.lostLocation || webhookData.LostLocation || input.lostLocation || input.LostLocation || '';
const dateOfLoss = webhookData.dateOfLoss || webhookData.DateOfLoss || input.dateOfLoss || input.DateOfLoss || '';
const studentNo = webhookData.studentNo || webhookData.StudentNo || input.studentNo || input.StudentNo || '';
const studentName = webhookData.studentName || webhookData.StudentName || input.studentName || input.StudentName || '';
const studentEmail = webhookData.studentEmail || webhookData.Email || input.studentEmail || input.Email || (studentNo ? `${studentNo}@ub.edu.ph` : '');

return [{
  json: {
    reportID: reportID,
    itemName: itemName,
    itemClass: itemClass,
    description: description,
    lostLocation: lostLocation,
    dateOfLoss: dateOfLoss,
    studentNo: studentNo,
    studentName: studentName,
    studentEmail: studentEmail,
    originalData: webhookData
  }
}];

const lostItem = $('Extract Lost Item').first().json;
const foundItemsResponse = $('Get All Found Items').first().json;
const foundItems = foundItemsResponse.data || foundItemsResponse || [];

// Filter: Only approved found items (StatusConfirmed = "1" or 1)
const approvedFoundItems = foundItems.filter(item =>
  (item.StatusConfirmed === "1" || item.StatusConfirmed === 1) &&
  (item.StatusConfirmed !== "-1" && item.StatusConfirmed !== -1)
);

// Limit to recent items (last 100) to avoid too many API calls
const recentFoundItems = approvedFoundItems.slice(0, 100);

// Format lost item for AI
const lostItemText = `Lost Item:
- Name: ${lostItem.itemName || 'Unknown'}
- Class: ${lostItem.itemClass || 'Unknown'}
- Description: ${lostItem.description || 'No description'}
- Location Lost: ${lostItem.lostLocation || 'Unknown'}
- Date Lost: ${lostItem.dateOfLoss || 'Unknown'}
- Student: ${lostItem.studentName || 'Unknown'} (${lostItem.studentNo || 'Unknown'})`;

// Format found items for AI
const foundItemsText = recentFoundItems.map((item, index) => {
  return `Found Item ${index + 1}:
- Name: ${item.ItemName || item.itemName || 'Unknown'}
- Class: ${item.ClassName || item.itemClass || 'Unknown'}
- Description: ${item.Description || item.description || 'No description'}
- Location Found: ${item.LocationFound || item.locationFound || item.Location || item.location || 'Unknown'}
- Date Found: ${item.DateFound || item.dateFound || 'Unknown'}`;
}).join('\n\n');

return [{
  json: {
    lostItem: lostItem,
    lostItemText: lostItemText,
    foundItems: recentFoundItems,
    foundItemsText: foundItemsText,
    totalFoundItems: recentFoundItems.length
  }
}];

