const aiResponse = $input.first().json;
const preparedData = $('Prepare AI Comparison').first().json;
const lostItem = preparedData.lostItem;
const foundItems = preparedData.foundItems;

// Extract AI response - handle different formats
let aiMatches = [];
let aiContent = '';

if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message) {
  aiContent = aiResponse.choices[0].message.content;
} else if (aiResponse.message && aiResponse.message.content) {
  aiContent = aiResponse.message.content;
} else if (aiResponse.content) {
  aiContent = aiResponse.content;
} else if (aiResponse.output) {
  aiContent = aiResponse.output;
} else if (typeof aiResponse === 'string') {
  aiContent = aiResponse;
}

// Clean and parse AI response
aiContent = aiContent.trim();
// Remove markdown code blocks if present
aiContent = aiContent.replace(/```json\n?/g, '');
aiContent = aiContent.replace(/```\n?/g, '');
aiContent = aiContent.replace(/^\s*\[\s*/s, '[');
aiContent = aiContent.replace(/\s*\]\s*$/s, ']');

try {
  aiMatches = JSON.parse(aiContent);
  if (!Array.isArray(aiMatches)) {
    aiMatches = [];
  }
} catch (e) {
  // If parsing fails, try to extract JSON from text
  const jsonMatch = aiContent.match(/\[[\s\S]*\]/);
  if (jsonMatch) {
    try {
      aiMatches = JSON.parse(jsonMatch[0]);
    } catch (e2) {
      aiMatches = [];
    }
  }
}

// Process matches and create notification data
const notifications = [];

for (const match of aiMatches) {
  if (match.isMatch && match.confidence >= 60 && match.index !== undefined) {
    const foundItem = foundItems[match.index];
    if (foundItem) {
      notifications.push({
        json: {
          studentNo: lostItem.studentNo || '',
          studentName: lostItem.studentName || 'Student',
          studentEmail: lostItem.studentEmail || (lostItem.studentNo ? `${lostItem.studentNo}@ub.edu.ph` : ''),
          lostItemName: lostItem.itemName || 'Unknown',
          lostItemID: lostItem.reportID || '',
          lostItemClass: lostItem.itemClass || '',
          lostItemDescription: lostItem.description || '',
          foundItemName: foundItem.ItemName || foundItem.itemName || 'Unknown',
          foundItemID: foundItem.ItemID || foundItem.itemID || '',
          foundItemClass: foundItem.ClassName || foundItem.itemClass || '',
          foundItemDescription: foundItem.Description || foundItem.description || '',
          matchConfidence: match.confidence,
          matchReasoning: match.reasoning || 'AI determined this is a potential match',
          timestamp: new Date().toISOString()
        }
      });
    }
  }
}

return notifications.length > 0 ? notifications : [{
  json: {
    noMatches: true,
    message: 'No matches found with confidence >= 60%',
    totalAnalyzed: foundItems.length,
    timestamp: new Date().toISOString()
  }
}];

const lostItem = $('Extract Lost Item').first().json;
const aiMatches = $('Process AI Matches').all();
const matchCount = aiMatches.filter(m => m.json.noMatches !== true).length;

return [{
  json: {
    success: true,
    message: `AI match detection completed. Found ${matchCount} potential match(es) for lost item '${lostItem.itemName}'`,
    lostItemID: lostItem.reportID,
    lostItemName: lostItem.itemName,
    matchesFound: matchCount,
    notificationsSent: matchCount,
    timestamp: new Date().toISOString()
  }
}];

const error = $input.first().json.error;
return [{
  json: {
    success: false,
    error: error?.message || error?.error || 'Unknown error',
    message: 'AI match detection failed'
  }
}];
